import "./index.scss";
import Logo from "./Logo";
import Scroll from "./Scroll";
const Home = ({ data }) => {
    /*
    <div className="player-card-container">
                    {data.map(player => (
                    <Link to={`/team/${player.team}`} key={player.id} className="player-card">
                        <div>{player.name}</div>
                        <div>{player.team}</div>
                        <div>{player.position}</div>
                    </Link>
                    ))}
                </div>
    */
   /*
   .player-card-container {
        display: flex;
        //overflow: hidden;
        padding: 20px 0;
        margin-top: 800px;
        white-space: nowrap;
      }

      .player-card {
        flex: 0 0 auto;
        display: inline-block;
        background-color: #fff;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
        margin-right: 15px;
        padding: 10px;
        width: 150px;
        text-align: center;
        text-decoration: none;
        color: inherit;
        animation: scroll 60s linear infinite;
        position: relative;
      }

      .player-card div {
        margin: 5px 0;
      }

      @keyframes scroll {
        0% {
          transform: translateX(100%);
        }
        100% {
          transform: translateX(-100vw);
        }
      }
   */
    return(
        <>
            <div className = "home-page">
                <div className = "text-zone">
                    <h1>Michigan Football Players in the NFL</h1>
                    <h2>Explore your favorite Michigan Alumni in the NFL</h2> 
                    <h2>See upcoming games, stats, and add their games to your calendar!</h2>
                    <Link to="/search" className="flat-button">GET STARTED</Link>
                </div>
                <Logo className="Home"/>
                <Scroll />
            </div>
        </>
    )
}

export default Home
