import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./index.scss";
import Logo from "./Logo";
const Home = () => {
    const [letterClass, setLetterClass] = useState("text-animate")
    const welcomeArray1 = "Welcome To".split("");
    const welcomeArray2 = "McGill Exam Scheduler!".split("");

    useEffect(() => {
        const timerId = setTimeout(() => {
            setLetterClass("text-animate-hover");
        }, 4000);

        return () => {
            clearTimeout(timerId);
        };
    }, []);

    return(
        <>
            <div className = "container home-page">
                <div className = "text-zone">
                    <h1>Welcome to the hub for Michigan Football Alumni</h1>
                    <h2>Explore you favorite Michigan Alumni in the NFL<h2/> 
                    <h3>Just search, click add, and export...</h3>
                    <Link to="/search" className="flat-button">GET STARTED</Link>
                    </h2>
                </div>
                <Logo className="Home"/>
            </div>
        </>
    )
}

export default Home
